import React, { Component } from "react";
import "./Styles/style.css"

const myContext = React.createContext();

class MyProvider extends Component {
  state = {
    stage: 1,
    players: [],
    result: "",
  };

  render() {
    return (
      <myContext.Provider
        value={{
          state: this.state,
        }}
      >
        {this.props.children}
      </myContext.Provider>
    );
  }
}

export{
    myContext,
    MyProvider
}
