import React, { Component } from "react";

const myContext = React.createContext();

class MyProvider extends Component {
  state = {
    stage: 1,
    players: [],
    result: "",
  };

  addPlayerHandler=(name)=>{
    this.setState( (prevState)=>({
            players:[
                ...prevState.players,
                name
            ]
    }))
  }

  render() {
    return (
      <myContext.Provider
        value={{
          state: this.state,
          addPlayers:this.addPlayerHandler
        }}
      >
        {this.props.children}
      </myContext.Provider>
    );
  }
}

export{
    myContext,
    MyProvider
}
